mapper.update:
update form_design
FORM_NAME = #{formName}, FORM_JSON = #{formJson}, CREATE_BY = #{createBy}, CREATE_DATE = #{createDate}, UPDATE_BY = #{updateBy}, UPDATE_DATE = #{updateDate}, REMARKS = #{remarks}, DEL_FLAG = #{delFlag}, FORM_CODE = #{formCode}, GEN_TABLE_NAME = #{genTableName}, FORM_URL = #{formUrl},
where ID = #{id}

UPDATE FORM_DESIGN SET 	
	form_json = #{formJson},
	form_html = #{formHtml},
	GEN_TABLE_NAME = #{genTableName},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE FORM_DESIGN SET 	
	form_data = #{formData}
WHERE id = #{id}

mapper.select:
SELECT count(0) 
FROM information_schema.TABLES 
WHERE table_name = #{tableName}

select count(0)
from information_schema.columns 
where TABLE_NAME = #{tableName}
AND COLUMN_NAME = #{columnName}

SELECT count(0)
FROM form_design a
WHERE a.form_code = #{formCode}
AND del_flag = ${@com.zjry.common.core.domain.BaseEntity@DEL_FLAG_NORMAL}
